import React, { useState } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';

function Post({ addPost }) {
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [image, setImage] = useState(null);
  const [ogImageUrl, setOgImageUrl] = useState('');
  const history = useHistory();

  const handleImageChange = (e) => {
    setImage(e.target.files[0]);
  };

  const generateOgImage = async () => {
    try {
      const formData = new FormData();
      formData.append('title', title);
      formData.append('content', content);
      formData.append('image', image);

      const response = await axios.post('http://localhost:3001/generate-og-image', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        },
        responseType: 'blob'
      });

      const url = URL.createObjectURL(response.data);
      setOgImageUrl(url);

      const newPost = {
        title,
        content,
        image,
        ogImageUrl: url
      };

      addPost(newPost);
      history.push('/posts');
    } catch (error) {
      console.error('Error generating OG image:', error);
    }
  };

  return (
    <div className="post">
      <h2>Create a Post</h2>
      <input
        type="text"
        placeholder="Title"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
      />
      <textarea
        placeholder="Content"
        value={content}
        onChange={(e) => setContent(e.target.value)}
      />
      <input type="file" onChange={handleImageChange} />
      {image && <img src={URL.createObjectURL(image)} alt="Post" />}
      <button onClick={generateOgImage}>Generate OG Image</button>
    </div>
  );
}

export default Post;
